mapper.insert:
insert into article
	set regDate = now()
		, updateDate = now()
		, memberId = #{loginedMemberId}
		, boardId = #{boardId}
		, title = #{title} 
		, `body` = #{body}

mapper.select:
SELECT a.* , m.nickname AS `writer` FROM article AS a INNER JOIN `member` AS m ON a.memberId = m.id where 1 = 1
and a.boardId = #{boardId }
ORDER BY id DESC

mapper.update:
update article

		updateDate = now()

			, title = #{title}


			, `body` = #{body}


	where id = #{id}

mapper.select:
SELECT COUNT(id)
	FROM article
	WHERE boardId = #{boardId}
